import React, { useState } from 'react';
import { createPost } from '../api/postApi';

const PostForm = ({ fetchPosts }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [confirmationMessage, setConfirmationMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const newPost = { title, content };
        console.log('Submitting post:', newPost); // Log the post data

        try {
            await createPost(newPost);
            setConfirmationMessage('Post submitted successfully!');
            await fetchPosts(); // Fetch and update the post list
        } catch (error) {
            console.error('Error submitting post:', error);
            setConfirmationMessage('Error submitting post. Please try again.');
        }

        setTitle('');
        setContent('');
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Content"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    required
                />
                <button type="submit">Submit</button>
            </form>
            {confirmationMessage && <p>{confirmationMessage}</p>} {/* Display confirmation message */}
        </div>
    );
};

export default PostForm;
