import React, { useEffect, useState } from 'react';

const PostForm = ({ fetchPosts, editingPost, setEditingPost }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    // Populate fields if editing an existing post
    useEffect(() => {
        if (editingPost) {
            setTitle(editingPost.title);
            setContent(editingPost.content);
        }
    }, [editingPost]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const postData = { title, content };

        try {
            if (editingPost) {
                // Update the post
                await fetch(`http://localhost:5000/api/posts/${editingPost.id}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(postData),
                });
                setEditingPost(null); // Reset editing state after update
            } else {
                // Create a new post
                await fetch('http://localhost:5000/api/posts', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(postData),
                });
            }

            // Reset the form fields
            setTitle('');
            setContent('');
            fetchPosts(); // Refresh the posts list
        } catch (error) {
            console.error('Error saving post:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)} 
                placeholder="Post Title" 
                required 
            />
            <textarea 
                value={content} 
                onChange={(e) => setContent(e.target.value)} 
                placeholder="Post Content" 
                required 
            />
            <button type="submit">{editingPost ? 'Update Post' : 'Create Post'}</button>
            {editingPost && <button type="button" onClick={() => setEditingPost(null)}>Cancel</button>}
        </form>
    );
};

export default PostForm;
