import React, { useEffect, useState } from 'react';
import { fetchPosts } from './api/postApi'; // Adjust the import path as needed
import PostForm from './components/PostForm'; // Adjust the import path as needed
import PostList from './components/PostList'; // Import the PostList component

const App = () => {
    const [posts, setPosts] = useState([]);
    const [editingPost, setEditingPost] = useState(null);

    const loadPosts = async () => {
        try {
            const response = await fetch('/api/posts');
            const fetchedPosts = await response.json();
            setPosts(fetchedPosts);
        } catch (error) {
            console.error('Error loading posts:', error);
        }
    };

    useEffect(() => {
        loadPosts(); // Fetch posts on component mount
    }, []);

    return (
        <div>
            <h1>Blog Posts</h1>
            <PostForm fetchPosts={loadPosts} editingPost={editingPost} setEditingPost={setEditingPost} />
            <PostList fetchPosts={loadPosts} />
        </div>
    );
};

export default App;
