import React, { useEffect, useState } from 'react';
import PostForm from './components/PostForm'; // Adjust the import path as needed
import PostList from './components/PostList'; // Import the PostList component

const App = () => {
    const [posts, setPosts] = useState([]);
    const [editingPost, setEditingPost] = useState(null); // State for editing post

    const loadPosts = async () => {
        try {
            const response = await fetch('/api/posts');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const fetchedPosts = await response.json();
            setPosts(fetchedPosts);
        } catch (error) {
            console.error('Error loading posts:', error);
        }
    };

    useEffect(() => {
        loadPosts(); // Fetch posts on component mount
    }, []);

    const handleEdit = (post) => {
        setEditingPost(post); // Set the post to be edited
    };

    const handleDelete = async (id) => {
        try {
            await fetch(`/api/posts/${id}`, {
                method: 'DELETE',
            });
            // Remove deleted post from state
            setPosts(posts.filter(post => post.id !== id));
        } catch (error) {
            console.error('Error deleting post:', error);
        }
    };

    return (
        <div>
            <h1>Blog Posts</h1>
            <PostForm fetchPosts={loadPosts} editingPost={editingPost} setEditingPost={setEditingPost} />
            <PostList posts={posts} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default App;
