import React, { useEffect, useState } from 'react';
import Post from './Post';

const PostList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch('http://localhost:5000/posts'); // Use full URL
                if (!response.ok) {
                    const errorText = await response.text(); // Get error response text
                    throw new Error(`Network response was not ok: ${response.status} - ${errorText}`);
                }
                const data = await response.json();
                setPosts(data);
            } catch (error) {
                console.error('Error fetching posts:', error);
                // Optionally, set an error state to display a message in the UI
            }
        };
    
        fetchPosts();
    }, []);
    

    

    const handleEdit = (id) => {
        // Here, you might want to navigate to the edit page
        console.log(`Edit post with ID: ${id}`);
        // Example: navigate(`/edit/${id}`);
    };

    const handleDelete = async (id) => {
        try {
            await fetch(`/posts/${id}`, {
                method: 'DELETE',
            });
            // Update state to remove the deleted post
            setPosts(posts.filter(post => post.id !== id));
        } catch (error) {
            console.error('Error deleting post:', error);
        }
    };

    if (loading) {
        return <p>Loading posts...</p>; // Loading message
    }

    if (error) {
        return <p>Error: {error}</p>; // Error message
    }

    return (
        <div>
            <h2>Posts</h2>
            {posts.length === 0 ? (
                <p>No posts available</p>
            ) : (
                posts.map(post => (
                    <Post key={post.id} post={post} onEdit={handleEdit} onDelete={handleDelete} />
                ))
            )}
        </div>
    );
};

export default PostList;
