// frontend/src/api/postApi.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/posts'; // Adjust the API URL as necessary

export const fetchPosts = async () => {
    try {
        const response = await axios.get(API_URL);
        return response.data; // Assuming your API returns the posts in the response data
    } catch (error) {
        console.error('Error fetching posts:', error);
        throw error; // Rethrow the error to handle it in the calling component
    }
};

export const createPost = async (newPost) => {
    try {
        const response = await axios.post(API_URL, newPost);
        return response.data; // Return the created post
    } catch (error) {
        console.error('Error creating post:', error);
        throw error; // Rethrow the error to handle it in the calling component
    }
};

export const loginUser = async (username, password) => {
    // Implement login logic if applicable
};
