import axios from 'axios';

const API_URL = 'http://localhost:5000/api/posts'; // Ensure this matches your backend URL

export const fetchPosts = async () => {
    try {
        const response = await axios.get(API_URL);
        return response.data; // Assuming your backend sends back the posts in the response body
    } catch (error) {
        console.error('Error fetching posts:', error.response ? error.response.data : error.message);
        throw error; // Rethrow the error for handling in the component
    }
};

// Function to create a new post
export const createPost = async (newPost) => {
    try {
        const response = await axios.post(API_URL, newPost);
        return response.data; // Assuming the backend returns the created post
    } catch (error) {
        console.error('Error creating post:', error.response ? error.response.data : error.message);
        throw error; // Rethrow the error for handling in the component
    }
};
