import React, { useState, useEffect } from 'react';

const PostForm = ({ fetchPosts, editingPost, setEditingPost }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    useEffect(() => {
        if (editingPost) {
            setTitle(editingPost.title);
            setContent(editingPost.content);
        } else {
            setTitle('');
            setContent('');
        }
    }, [editingPost]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const postData = { title, content };

        try {
            const response = editingPost 
                ? await fetch(`http://localhost:5000/api/posts/${editingPost.id}`, {
                    method: 'PUT', // Use PUT for updating
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(postData),
                })
                : await fetch('http://localhost:5000/api/posts', {
                    method: 'POST', // Use POST for creating
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(postData),
                });

            if (response.ok) {
                await fetchPosts(); // Refresh posts list
                setEditingPost(null); // Clear editing state
                setTitle('');
                setContent('');
            } else {
                console.error('Failed to add/update post');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                placeholder="Title" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)} 
                required 
            />
            <textarea 
                placeholder="Content" 
                value={content} 
                onChange={(e) => setContent(e.target.value)} 
                required 
            />
            <button type="submit">{editingPost ? 'Update' : 'Add Post'}</button>
        </form>
    );
};

export default PostForm;
