import React, { useState, useEffect } from 'react';

const PostForm = ({ fetchPosts, editingPost, setEditingPost }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    useEffect(() => {
        if (editingPost) {
            setTitle(editingPost.title);
            setContent(editingPost.content);
        } else {
            setTitle('');
            setContent('');
        }
    }, [editingPost]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const postData = { title, content };

        if (editingPost) {
            // Update post logic
            await fetch(`/api/posts/${editingPost.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(postData),
            });
            setEditingPost(null); // Clear editing state
        } else {
            // Create new post
            await fetch('/api/posts', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(postData),
            });
        }
        fetchPosts(); // Re-fetch posts after adding/updating
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
            />
            <textarea
                placeholder="Content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
            />
            <button type="submit">{editingPost ? 'Update' : 'Add'} Post</button>
        </form>
    );
};

export default PostForm;
