import React, { useEffect, useState } from 'react'; // Ensure useEffect and useState are imported
import Post from './Post'; // Make sure you import the Post component

const PostList = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/posts'); // Updated URL
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setPosts(data);
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };
    
        fetchPosts();
    }, []);
    

    const handleEdit = (id) => {
        console.log(`Edit post with ID: ${id}`);
    };

    const handleDelete = async (id) => {
        await fetch(`/api/posts/${id}`, {
            method: 'DELETE',
        });
        setPosts(posts.filter(post => post.id !== id));
    };

    return (
        <div>
            <h2>Posts</h2>
            {posts.map(post => (
                <Post key={post.id} post={post} onEdit={handleEdit} onDelete={handleDelete} />
            ))}
        </div>
    );
};

export default PostList;
