import React, { useState } from 'react';
import PostList from './components/PostList';
import PostForm from './components/PostForm';

const App = () => {
    const [editingPost, setEditingPost] = useState(null);

    const handleEdit = (post) => {
        setEditingPost(post);
    };

    const handleDelete = (id) => {
        fetch(`/posts/${id}`, {
            method: 'DELETE',
        }).then(() => {
            // Optionally, refresh the post list after deletion
        });
    };

    const handleSubmit = (newPost) => {
        const method = editingPost ? 'PUT' : 'POST';
        const url = editingPost ? `/posts/${editingPost.id}` : '/posts';

        fetch(url, {
            method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newPost),
        }).then(() => {
            setEditingPost(null);
            // Optionally, refresh the post list after submission
        });
    };

    return (
        <div>
            <h1>Blogging Platform</h1>
            <PostForm postToEdit={editingPost} onSubmit={handleSubmit} />
            <PostList onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default App;
